mapper.select:
select

from sys_button
where 1=1

    and id=#{id,jdbcType=INTEGER}


    and title like concat('%',#{title},'%')


    and name like concat('%',#{name},'%')


    and icon = #{icon,jdbcType=VARCHAR}


    and method_name = #{methodName,jdbcType=VARCHAR}


    and method_param = #{methodParam,jdbcType=VARCHAR}


    and size = #{size,jdbcType=VARCHAR}


    and type = #{type,jdbcType=VARCHAR}


    and css = #{css,jdbcType=VARCHAR}


    and menu_id = #{menuId,jdbcType=VARCHAR}


    and remake = #{remake,jdbcType=VARCHAR}


    and created_by = #{createdBy,jdbcType=VARCHAR}


    and created_time = #{createdTime,jdbcType=TIMESTAMP}


    and updated_by = #{updatedBy,jdbcType=VARCHAR}


    and updated_time = #{updatedTime,jdbcType=TIMESTAMP}


    and enabled = #{enabled,jdbcType=BIT}


    and sort = #{sort,jdbcType=INTEGER}


    and menu_type = #{menuType,jdbcType=INTEGER}

order by menu_id asc ,menu_type asc,sort asc
